import { useSelector } from "react-redux";
import { useCart } from "react-use-cart";
import './cart.css'

const Cart = () => {
    const {
        isEmpty,
        totalUniqueItems,
        items,
        updateItemQuantity,
        removeItem,
    } = useCart();
    console.log(items)
    if (isEmpty) return <p>Your cart is empty</p>;

    return (
        <>
            <h1>Cart ({totalUniqueItems})</h1>

            <div className="whole-cart-page">
                {items.map((item) => (
                    <div className="whole-cart" key={item.id}>
                        <div>
                        <div className="left-side">
                            <img width={'100px'} src={item.src} alt="Product img" />
                            {item.name}
                            <button onClick={() => removeItem(item.id)}>&times;</button>
                        </div>
                        <div className="right-side">
                            {/* {item.quantity} x {item.name} &mdash; */}
                            {/* {Number(item.quantity) * Number(item.price.slice(4))} */}
                            <button
                                onClick={() => updateItemQuantity(item.id, (item.quantity ?? 0) - 1)}
                            >
                                -
                            </button>
                            {item.quantity}
                            <button
                                onClick={() => updateItemQuantity(item.id, (item.quantity ?? 0) + 1)}
                            >
                                +
                            </button>
                        </div>
                        </div>
                        <div className="right-section">
                            <div>CART SUMMARY</div>
                            <div>
                            <div>Subtotal</div>
                            <div>{EGP }</div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </>
    );
}
export default Cart;
