const InitialState = { array1: [] }

export const reducer = (state = InitialState, action) => {
    switch (action.type) {
        case "supermarket":
            return { array1: action.payload };
        // case "Fashion":
        //     return { ...state, array1: action.Cat2 };
        // case "HealthBeauty":
        //     return { ...state, array1: action.Cat3 };
        default: return { state };
    }
}

