// import { ProductsPage } from '../../../Data/data';
// import { action1 } from '../actions/ReduxActions';




const InitialState = { array1: [], array2: [] }

export const reducer = (state = InitialState, action) => {
    switch (action.type) {
        case "supermarket":
            return { array1: action.Cat };
        case "Fashion":
            return { array1: action.Cat2 };
        case "HealthBeauty":
            return { array1: action.Cat3 };
        default: return { state };
    }
}

