export const appear = () => {
    return (dispatch) => {
        var NewArray = []
        for (let i in Categories.supermarket) {
            for (let f in Categories.supermarket[i]) {
                for (let g in Categories.supermarket[i][f]) {
                    const value = Categories.supermarket[i][f][g]
                    NewArray.push(value)
                    // dispatch({ type: 'supermarket', Cat: Categories.supermarket })
                    // dispatch({ type: 'Fashion', Cat2: Categories.Fashion })
                    // dispatch({ type: 'HealthBeauty', Cat3: Categories.Fashion })
                }
            }
        }
        dispatch({
            type: 'supermarket',
            payload: NewArray
        });
    };
}
