export const appearCat = (dispatch, Cat) => {
    const NewArray = []
    for (let i in Cat) {
        for (let f in Cat[i]) {
            for (let g in Cat[i][f]) {
                NewArray.push(Cat[i][f][g])
                dispatch({ click, Cat: Categories.supermarket })
                dispatch({ type: 'Fashion', Cat: Categories.Fashion })
                dispatch({ type: 'HealthBeauty', Cat: Categories.Fashion })
            }
        }
    }
}
